import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import Button from '@material-ui/core/Button';

const AllCampusesView = ({ allCampuses, deleteCampus }) => {
  return (
    <div>
      <h1>All Campuses</h1>
      
      <Link to="/campuses/add">
        <Button variant="contained" color="primary" style={{ marginBottom: '20px' }}>
          Add New Campus
        </Button>
      </Link>

      {!allCampuses.length ? (
        <p>There are no campuses.</p>
      ) : (
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
          {allCampuses.map((campus) => (
            <div key={campus.id} style={{
              border: '1px solid #ddd',
              borderRadius: '8px',
              padding: '15px',
              width: '300px'
            }}>
              <Link to={`/campus/${campus.id}`}>
                <img 
                  src={campus.imageUrl || '/default-campus.jpg'} 
                  alt={campus.name}
                  style={{ width: '100%', height: '200px', objectFit: 'cover' }}
                />
                <h2>{campus.name}</h2>
              </Link>
              <p><strong>Address:</strong> {campus.address}</p>
              <p>{campus.description}</p>
              
              <div style={{ marginTop: '10px' }}>
                <Button 
                  variant="contained" 
                  color="secondary"
                  onClick={() => deleteCampus(campus.id)}
                >
                  Delete
                </Button>
                <Link to={`/campus/${campus.id}/edit`} style={{ marginLeft: '10px' }}>
                  <Button variant="contained" color="primary">
                    Edit
                  </Button>
                </Link>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

AllCampusesView.propTypes = {
  allCampuses: PropTypes.array.isRequired,
  deleteCampus: PropTypes.func.isRequired,
};

export default AllCampusesView;
