/* ===== /src/store/actions/campusActions.js ===== */
import * as at from "./actionTypes";
import axios from "axios";

// Fetch all campuses
export const fetchAllCampuses = () => async dispatch => {
  try {
    const { data } = await axios.get("/api/campuses");
    dispatch({ type: at.FETCH_ALL_CAMPUSES, payload: data });
  } catch (error) {
    console.error("Error fetching campuses:", error);
  }
};

// Add new campus
export const addCampus = (campusData) => async dispatch => {
  try {
    const { data } = await axios.post("/api/campuses", campusData);
    dispatch({ type: at.ADD_CAMPUS, payload: data });
  } catch (error) {
    console.error("Error adding campus:", error);
    throw error;
  }
};

// Delete campus
export const deleteCampus = (campusId) => async dispatch => {
  try {
    await axios.delete(`/api/campuses/${campusId}`);
    dispatch({ type: at.DELETE_CAMPUS, payload: campusId });
  } catch (error) {
    console.error("Error deleting campus:", error);
  }
};

// Edit campus
export const editCampus = (campusId, campusData) => async dispatch => {
  try {
    const { data } = await axios.put(`/api/campuses/${campusId}`, campusData);
    dispatch({ type: at.EDIT_CAMPUS, payload: data });
  } catch (error) {
    console.error("Error editing campus:", error);
    throw error;
  }
};
